class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> mpp1;
        for(int i=0;i<n;i++){
            unordered_map<long long, long long> mpp2; 
            if (nums[i] == k) {
                ans++;
            }
            mpp2[nums[i]]++;
            for (auto it : mpp1) {
                long long val = it.first;
                long long freq = it.second;
                long long helo = val & nums[i];
                if (helo == k) {
                    ans += freq;
                }
                mpp2[helo] += freq;
            }
            mpp1.clear();
            for(auto it : mpp2){
                mpp1[it.first] = it.second;
            }
        }
        return ans;
    }
};
