class Solution {
public:
        #define ll long long
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<ll,ll>mp;
        ll count=0;
        ll n=nums.size();
        ll r=0;
        while(r<n){
            unordered_map<ll,ll>new_mp;
            ll curElement=nums[r];
            if(curElement==k)  count++;
            new_mp[curElement]=1; // adding new element

            //update karenge old map ko
            for(auto x: mp){
                ll newAnd=x.first&curElement;
                ll freq=x.second;
                if(newAnd==k) count+=freq;
                new_mp[newAnd]+=freq;
            }
            mp=new_mp;
            r++;
        }
        return count;
    }
};
