#include<bits/stdc++.h>
#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        ll counts=0;
        map<ll, ll> olds;
        for (int i=0; i<n; i++) {
            map<ll, ll> news;
            if (nums[i]==k) {
                counts++;
            }
            news[nums[i]]=1;
            for (auto&[val,freq]: olds) {
                ll newAndResult=val&nums[i];
                if (newAndResult==k) counts+=freq;
                news[newAndResult]+=freq;
            }
            olds=news;

        }
        return counts;
    }
};
