class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;

        unordered_map<long long, long long> pre;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currResults; 

            if (nums[i] == k) {
                ++count;
            }
            currResults[nums[i]] = 1;

            for (auto& [val, freq] : pre) {
                long long ne = val & nums[i];
                if (ne == k) {
                    count += freq;
                }
                currResults[ne] += freq;
            }

            pre = currResults;
        }

        return count;
    }
};
