#define ll long long

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll cnt = 0;

        unordered_map<ll, ll> prev;

        for (int i = 0; i < n; ++i) {
            unordered_map<ll, ll> curr; 

            if (nums[i] == k) {
                ++cnt;
            }
            curr[nums[i]] = 1;

            for (auto& it: prev) {
                ll val = it.first, freq = it.second;
                ll next = val & nums[i];
                if (next == k) {
                    cnt += freq;
                }
                curr[next] += freq;
            }

            prev = curr;
        }

        return cnt;
    }
};

