class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {
        int n = nums.size();
        long long total = 0;
        unordered_map<long long, long long> prev;
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> current; 
            if (nums[i] == target) {
                ++total;
            }
            current[nums[i]] = 1;
            for (auto& [value, frequency] : prev) {
                long long newAndResult = value & nums[i];
                if (newAndResult == target) {
                    total += frequency;
                }
                current[newAndResult] += frequency;
            }

            prev = current;
        }
        return total;
    }
};
