class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size() ;
        long long total = 0 ;
        unordered_map<long long,long long> previousResults;
        for(int idx = 0 ; idx < n ; idx++){
            unordered_map<long long,long long > currentResults ;
            if(nums[idx] == k) total++ ;
            currentResults[nums[idx]] = 1 ;
            for(auto&[value,frequency] : previousResults){
                long long newRes = value &nums[idx];
                if(newRes == k ){
                    total += frequency ;
                }
                currentResults[newRes]+= frequency ;
            }
            previousResults = currentResults ;
        }
        return total ;
    }
};
