class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        length = len(nums)
        total = 0
        previous = defaultdict(int)

        for element in nums:
            current = defaultdict(int) 
            if element == k:
                total += 1
            current[element] = 1

            for value, frequency in previous.items():
                new_and_result = value & element
                if new_and_result == k:
                    total += frequency
                current[new_and_result] += frequency

            previous = current

        return total
