class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        size = len(nums)
        t = 0
        res = defaultdict(int)
        for i in range(size):
            ans = defaultdict(int)
            if nums[i] == k:
                t += 1
            ans[nums[i]] = 1 
            for v, f in res.items():
                n = v & nums[i]
                if n == k:
                    t += f
                ans[n] += f
            res = ans
        return t
