from typing import List
from collections import defaultdict

class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        count = 0
        n = len(nums)


        and_map = defaultdict(int)

        for i in range(n):
            new_and_map = defaultdict(int)
            current_and = nums[i]


            if current_and == k:
                count += 1

            new_and_map[current_and] += 1


            for and_val in and_map:
                new_and_val = and_val & nums[i]
                new_and_map[new_and_val] += and_map[and_val]
                if new_and_val == k:
                    count += and_map[and_val]


            and_map = new_and_map

        return count
