#define ll long long 

class Solution {
public:
    ll countSubarrays(vector<int>& v, int tar ) {
        ll n = v.size();
        unordered_map<ll, ll> mp;
        ll ans = 0;
        ll i=0;
        while ( i<n ) {
            unordered_map<ll, ll> mp1; 
            if (v[i] == tar) {
                ans+=1;
            }
            mp1[v[i]] = 1;
            for (auto it : mp) {
                ll val= it.first;
                ll freq= it.second;
                ll res = val & v[i];
                if ( res == tar) {
                    ans += freq;
                }
                mp1[ res] += freq;
            }
            mp = mp1;
            i++;
        }
        return ans;
    }
};
