class Solution {
public:
    long long solve(unordered_map<long long, long long> &previousRes,vector<int>& nums, int k){
        // unordered_map<long long, long long> previousRes;
        long long int cnt =0;
        int n =nums.size();
        long long ans=0;
    for (int i =0;i <=n-1; i++) {

        unordered_map<long long, long long> currentRes;

        currentRes[nums[i]]=1;

         if (k==nums[i]) {
              ++cnt;
           }

        for (auto& [val, freq]: previousRes) {
               long long finalRes = val &nums[i];


              if (k==finalRes) {
                   cnt=cnt+freq;
                }
              currentRes[finalRes] =freq+currentRes[finalRes];
            }

           previousRes = currentRes;
        }
        ans=cnt;
 return ans;
    }

    long long countSubarrays(vector<int>& nums, int k) {
