class Solution {
public:
    long long solve(vector<int>& nums, int k){
        int n = nums.size();
        long long count = 0;
        unordered_map<long long int, long long int> m1;

        for (int i=0; i<n; i++) {
            int ele = nums[i];
            unordered_map<long long int, long long int> m2;

            for(auto& pair : m1){
                long long int index = pair.first & ele;
                m2[index] = m2[index] + pair.second;
            }

            m2[ele]++;
            m1 = m2;

            if(m1.find(k) != m1.end()){
                count = count + m1[k];
            }
        }

        return count;
    }

    long long countSubarrays(vector<int>& nums, int k) {
        return solve(nums, k);
    }
};
