class Solution {
public:
    typedef long long ll;
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll ans=0;
        unordered_map<ll,ll> m1;
        for(int i = 0;i<nums.size();++i){
            unordered_map<ll,ll> m2;
            m2[nums[i]]=1;
            if(nums[i]==k) ans++;
            for(auto x : m1){
                ll val = (nums[i] & x.first);
                if(val==k){
                    ans+=x.second;
                }
                m2[val]+=x.second;
            }
            m1=m2;
        }
        return ans;

    }
};
