class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;

        long count = 0;

        Map<Long, Long> prevResults = new HashMap<>();
        for (int i = 0; i < n; ++i) {
            Map<Long, Long> currResult = new HashMap<>();
            if (nums[i] == k) {
                ++count;
            }
            currResult.put((long) nums[i], 1L);

            for (Map.Entry<Long, Long> entry : prevResults.entrySet()) {
                long val = entry.getKey();
                long freq = entry.getValue();
                long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    count += freq;
                }
                currResult.put(newAndResult, currResult.getOrDefault(newAndResult, 0L) + freq);
            }
            prevResults = currResult;
        }

        return count;
    }
}
