class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        d = defaultdict(list)
        d[nums[0]] = [0, 0]
        ans = 0
        if k in d:
            ans = d[k][-1] - d[k][0] + 1
        for i, x in enumerate(nums[1:], 1):
            tmp = defaultdict(list)
            for ki, v in d.items():
                l, r = v
                if ki & x in tmp:
                    l1, r1 = tmp[ki & x]
                    tmp[ki & x] = [min(l1, l), max(r1, r)]
                else:
                    tmp[ki & x] = [l, r]
            if x in tmp:
                l1, r1 = tmp[x]
                tmp[x] = [min(l1, i), max(r1, i)]
            else:
                tmp[x] = [i, i]
            if k in tmp:
                ans += tmp[k][-1] - tmp[k][0] + 1
            d = tmp
        return ans
