class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size=nums.size();
        long long t=0;
        unordered_map<long long, long long> prev;
        for(int i=0;i<size;++i)
        {
            unordered_map<long long, long long> curr;
            if(nums[i]==k)
            {
                ++t;
            }
            curr[nums[i]]=1;
            for (auto& [value, frequency] : prev) {
                long long newres= value & nums[i];
                if (newres == k) {
                    t += frequency;
                }
                curr[newres] += frequency;
            }

            prev = curr;
        }
        return t;
        }


    };
