class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;
        map<long long, long long> prev;
        for (int i = 0; i < n; i++) {
            map<long long, long long> curr; 
            if (nums[i] == k) {
                cnt++;
            }
            curr[nums[i]] = 1;
            for (auto [x, y] : prev) {
                long long z = x & nums[i];
                if (z == k) {
                    cnt += y;
                }
                curr[z] += y;
            }
            prev = curr;
        }
        return cnt;
    }
};
