#define ll long long
class Solution {
public:
    ll countSubarrays(vector<int>&nums,int k) {
        int n = nums.size();
        ll ans = 0;
        unordered_map<ll,ll> mp1,mp2;

        for (int i = 0; i < n; ++i) {
            mp2.clear(); 
            if(nums[i]==k) ans++;
            mp2[nums[i]]=1;

            for (auto it: mp1) {
                ll nxt=it.first&nums[i];
                if(nxt == k) ans += it.second;
                mp2[nxt] += it.second;
            }

            mp1 = mp2;
        }

        return ans;
    }
};
