class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
           int n = nums.size();
       long long c=0;
        unordered_map<long long,long long>m;
        for(int i=0;i<n;i++){
            unordered_map<long long,long long>mp;
            if(nums[i]==k){
                c++;
            }
            mp[nums[i]]=1;
            for(auto &[v,f]:m){
                long long na=v&nums[i];
                if(na==k){
                    c+=f;
                }
                mp[na]+=f;
            }
            m=mp;
        }
        return c;
    }
};
