class Solution {
    using ll = long long;
public:
    long long countSubarrays(vector<int>& nums, int k) {
       int n = nums.size();
        ll cnt = 0;

        unordered_map<ll, ll> mpp;
        for (int i = 0; i < n; i++) {
             unordered_map<ll, ll> curr;
            if (nums[i] == k) {
                cnt++;
            }
            curr[nums[i]] = 1;
            for (auto& [key, freq] : mpp) {
                ll ind = key & nums[i];
                if (ind == k) {
                   cnt += freq;
                }
                curr[ind] += freq;
            }

            mpp = curr;
        }
        return cnt;
    }
};
