#define ll long long int
class Solution {
public:
    long long countSubarrays(vector<int>& arr, ll k) {
      int n = arr.size();
    ll cnt = 0;
    map<ll, ll> prev;

    for (int i=0; i<n; i++) {
        map<ll,ll> curr;
        ll target=k;

        if (arr[i]==target) {
            cnt++;
        }
        curr[arr[i]] = 1;

        for ( auto it : prev) {
            ll val = it.first;
            ll freq = it.second;
            ll newans = val & arr[i];
            if (newans == target) {
                cnt += freq;
            }
            curr[newans] += freq;
        }
        prev=curr;
    }
    return cnt;

    }
};
