class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long c=0;
        int n=nums.size();
        unordered_map<long , long long>m;
        for(int i=0;i<n;i++){
            unordered_map<long , long long>m2;
            if(nums[i]==k)c++;
            m2[nums[i]]=1;
            for(auto& [v,f]:m){
                long long x=v&nums[i];
                if(x==k)c+=f;
                m2[x]+=f;
            }
            m=m2;
        }
        return c;
    }
};
