class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<long long, long long> prev;
        int n = nums.size();
        long long ans = 0;

        for (int i = 0; i<n; ++i) {
            unordered_map<long long, long long> curr; 
            if (nums[i] == k) {
                ++ans;
            }
            curr[nums[i]] = 1;
            for (auto& [num, freq] : prev) {
                long long res = num & nums[i];
                if (res == k) {
                    ans += freq;
                }
                curr[res] += freq;
            }

            prev = curr;
        }
        return ans; 
    }
};
