class Solution {
    public long countSubarrays(int[] nums, int k) {
        int podavu = nums.length;
        long ans_count = 0,tempAns = 0;
        HashMap<Long, Long> mp = new HashMap<>();


        for(int num : nums) {
            tempAns = num;
            HashMap<Long, Long> mp2 = new HashMap<>();
            mp2.put(tempAns, mp2.getOrDefault(tempAns, 0L) + 1);
            if(tempAns == k) ans_count++;
            for (Map.Entry<Long, Long> entry : mp.entrySet()) {
                long freq = entry.getValue();
                long mp_value = entry.getKey();
                long new_temp = mp_value & tempAns;

                if (new_temp == k) ans_count += freq;

                mp2.put(new_temp, mp2.getOrDefault(new_temp, 0L) + freq);
            }

            mp = mp2;
        }
        return ans_count;
    }
}
