class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        unordered_map<long long, long long> mp;

        for (int x=0; x<nums.size(); x++) {
            unordered_map<long long, long long> curmp;
            if (nums[x] == k) ans++;
            curmp[nums[x]]=1;

            for (auto&j: mp) {
                long long bitres = j.first & nums[x];
                if (bitres == k) ans+=j.second;
                curmp[bitres]+=j.second;
            }
            mp = curmp;
        }

        return ans;

    }        
};
