class Solution {
public:
    long long surajsoln(vector<int>&nums,int k,unordered_map<long long,long long>&val){
        long long soln =0;
    for(int i =0;i<nums.size();i++){
           unordered_map<long long,long long>found;
            if(nums[i]==k){
                soln++;
            }
            found[nums[i]]=1;
            for(auto it:val){
               long long x =it.first&nums[i];
                if(x==k)soln+=it.second;

            found[x]+=it.second;
            }

        val =found;
    }
return soln;
}
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans =0;
        unordered_map<long long,long long>val;
        ans =surajsoln(nums,k,val);
        return ans;}
};
