class Solution {
    public long countSubarrays(int[] nums, int k) {
        int sZZe = nums.length;
        long TtL = 0;
        HashMap<Long, Long> PrS = new HashMap<>();

        for (int index = 0; index < sZZe; ++index) {
            HashMap<Long, Long> CrS = new HashMap<>();

            if (nums[index] == k) {
                ++TtL;
            }

            CrS.put((long) nums[index], 1L);

            for (HashMap.Entry<Long, Long> entry : PrS.entrySet()) {
                long value = entry.getKey();
                long frequency = entry.getValue();
                long newAndResult = value & nums[index];

                if (newAndResult == k) {
                    TtL += frequency;
                }

                CrS.put(newAndResult, CrS.getOrDefault(newAndResult, 0L) + frequency);
            }

            PrS = CrS;
        }

        return TtL;
    }
}
