class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {
        int n = nums.size();
        long long total = 0;
        unordered_map<long long, long long> prev_result;
        for (int i = 0; i < n; i++) {
            unordered_map<long long, long long> currentResults; 
            if (nums[i] == target) {
                ++total;
            }
            currentResults[nums[i]] = 1;
            for (auto& [value, frequency] : prev_result) {
                long long newAndResult = value & nums[i];
                if (newAndResult == target) {
                    total += frequency;
                }
                currentResults[newAndResult] += frequency;
            }

            prev_result = currentResults;
        }
        return total;
    }
};
