class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        int n=v.size();
        long long ans=0;
        unordered_map<long long,long long> pr;
        for(int i=0;i<n;i++){
            unordered_map<long long,long long> cr;
            int nd=v[i];
            if(nd==k) ans++;
            cr[nd]=1;
            for(auto x:pr){
                long long tp=x.first & nd;
                if(tp==k) ans+=x.second;
                cr[tp]+=x.second;
            }
            pr=cr;
        }
        return ans;
    }
};
