class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0;
        unordered_map<long long,long long> prev;
        for (int i=0;i<n;i++) 
        {
            unordered_map<long long,long long>curr; 
            curr[nums[i]]=1;
            if(nums[i]==k)
            {
                ans++;
            }
            for (auto it: prev) 
            {
                long long temp=it.first&nums[i];
                curr[temp]+=it.second;
                if(temp==k)
                {
                    ans+=it.second;
                }
            }
            prev=curr;
        }
        return ans;
    }
};
