class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans=0;
        map<long long,long long> prev;
        map<long long ,long long> temp;
        for(int i=0;i<n;i++)
        {
            if(nums[i]==k)
            {
                ans++;
            }
            temp[nums[i]]=1;
            for(auto it : prev )
            {
                long long next = it.first & nums[i];
                if(next == k)
                {
                    ans+=it.second;
                }
                temp[next]+=it.second;
            }
            prev = temp;
            temp.clear();
        }
        return ans;
    }
};
