class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long count=0;
        unordered_map<long long, long long>mp;
        for (int i=0; i<nums.size(); i++) {
            unordered_map<long long,long long>mp2;
            long long x=k;
            if (nums[i]==x)    count++;
            mp2[nums[i]]=1;
            for (auto& v : mp) {
                long long j= v.first & nums[i]; 
                if (j==x) count+= v.second;
                mp2[j] += v.second;
            }
            mp=mp2;
        }
        return count;
    }
};
