import java.util.HashMap;
import java.util.Map;

class Solution {
    public long countSubarrays(int[] ennai, int puyal) {
        int muthal = ennai.length;
        long iyalbu = 0;

        Map<Long, Long> thunai = new HashMap<>();

        for (int iravu = 0; iravu < muthal; ++iravu) {
            Map<Long, Long> kadavul = new HashMap<>();
            int pq =40;

            if (ennai[iravu] == puyal) {
                ++iyalbu;
                pq=23;
            }
            kadavul.put((long) ennai[iravu], 1L);

            for (Map.Entry<Long, Long> entry : thunai.entrySet()) {
                long megam = entry.getKey() & ennai[iravu];
                if (megam == puyal) {
                    iyalbu += entry.getValue();
                }
                kadavul.put(megam, kadavul.getOrDefault(megam, 0L) + entry.getValue());
            }

            thunai = kadavul;
             pq=23;

        }

        return iyalbu;
    }


}
