class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long count = 0;
        unordered_map<int, int> andMap;  // HashMap to store AND results and their frequencies

        for (int i = 0; i < nums.size(); ++i) {
            unordered_map<int, int> newMap;
            newMap[nums[i]] = 1;

            for (auto& [val, freq] : andMap) {
                int newAnd = val & nums[i];
                newMap[newAnd] += freq;
            }

            for (auto& [val, freq] : newMap) {
                if (val == k) {
                    count += freq;
                }
            }

            andMap = newMap;  // Update the hashmap with the new results
        }

        return count;
    }
};
