#define ll long long
#include <vector>
#include <map>
#include <iostream>

using namespace std;

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<ll, ll> possible;
        int n = nums.size();
        ll cnt = 0;

        for (int i = n - 1; i >= 0; i--) {
            if (nums[i] == k) {
                cnt++;
            }

            map<ll, ll> st;
            int cur = nums[i];

            for (auto val : possible) {
                if ((val.first & cur) == k) {
                    cnt += val.second;
                }
                int t = val.first & cur;

                st[(cur & val.first)] += val.second;
            }

            st[nums[i]]++;
            possible = st;


        }
        return cnt;
    }
};
