class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;
        unordered_map<int, int> andFreq;

        for (int i = 0; i < n; ++i) {
            unordered_map<int, int> newAndFreq;
            for (auto& [andVal, freq] : andFreq) {
                int newAndVal = andVal & nums[i];
                newAndFreq[newAndVal] += freq;
            }
            newAndFreq[nums[i]]++;
            andFreq = newAndFreq;

            if (andFreq.find(k) != andFreq.end()) {
                count += andFreq[k];
            }
        }

        return count;
    }
};
