class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size = nums.size();
        long long s = 0;
        unordered_map<long long, long long> before;
        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> after; 
            if (nums[index] == k) {
                ++s;
            }
            after[nums[index]] = 1;
            for (auto& [value, frequency] : before) {
                long long up = value & nums[index];
                if (up == k) {
                    s += frequency;
                }
                after[up] += frequency;
            }
            before = after;
        }
        return s;
    }
};
