class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long lll = 0;
        int aa = nums.size();
        map<long long, long long> sss;
        // map<long long, int> sss;
        for (int i = 0; i < aa; i++) {
            map<long long, long long> ggg; 
            if (nums[i] == k) ++lll;
            // if (nums[i] == k) ++lll{++j};
            ggg[nums[i]] = 1;
            for (auto& [value, frequency] : sss) {
                long long newAndResult = value & nums[i];
                // if (nums[i] == k) ++lll;
                // ggg[nums[i]] = 1;
                if (newAndResult == k) lll =lll+ frequency;
                ggg[newAndResult] = ggg[newAndResult] + frequency;
            }
            // ggg[newAndResult] = ggg[newAndResult] + frequency;

            sss = ggg;
        }
        return lll;
    }
};
