class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> prev;
        for (int i=0;i<n;i++) {
            unordered_map<long long, long long> curr; 
            if (nums[i] == k) {
                ans++;
            }
            curr[nums[i]] = 1;
            for (auto it : prev) {
                long long val = it.first;
                long long f = it.second;
                long long temp = val & nums[i];
                if (temp == k) {
                    ans += f;
                }
                curr[temp] += f;
            }

            prev = curr;
        }
        return ans;
    }

};
