class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        int s = nums.size();
        long long tot = 0;

        unordered_map<long long, long long> pre;

        for (int ind = 0; ind < s ;ind++) 
        {

            unordered_map<long long, long long> cur; 

            if (nums[ind] == k) 
            {
                tot++;
            }

            cur[nums[ind]] = 1;

            for (auto& [val, freq] : pre) 
            {
                long long newRes = val & nums[ind];
                if (newRes == k) {
                    tot += freq;
                }
                cur[newRes] += freq;
            }

            pre = cur;
        }
        return tot;

    }
};
