#define fn(i, n) for (int i = 0; i < n;i++)
class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        int n = arr.size();
        long long res = 0;
        unordered_map<long long, long long> mp;
        fn(i, n){
            unordered_map<long long, long long> mp2; 
            if (arr[i] == k) {
                ++res;
            }
            mp2[arr[i]] = 1;
            for (auto& it : mp) {
                long long ans = it.first & arr[i];
                if (ans == k) {
                    res += it.second;
                }
                mp2[ans] += it.second;
            }

            mp = mp2;
        }
        return res;
    }
};
