#include <vector>
#include <unordered_map>

class Solution {
public:
    long long countSubarrays(std::vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;

        std::unordered_map<long long, long long> pResults;

        for (int i = 0; i < n; ++i) {
            std::unordered_map<long long, long long> cResults; 

            if (nums[i] == k) {
                ++cnt;
            }
            cResults[nums[i]] = 1;

            for (auto& [val, freq] : pResults) {
                long long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    cnt += freq;
                }
                cResults[newAndResult] += freq;
            }

            pResults = cResults;
        }

        return cnt;
    }
};
