class Solution {
public:
#define ll long long
    long long countSubarrays(vector<int>& arr, int k) {
    int n = arr.size();
    ll ans = 0;
    unordered_map<ll, ll> mpp;
    unordered_map<ll,ll> mpp2;
    bool flag=true;
    ll rem=0;
    for (int i = 0; i < n; i++) {
        unordered_map<ll, ll> curr; 
        if (arr[i] == k) {
            ans++;
            flag=true;
        }
        else if(flag!=true) break;
        curr[arr[i]]++;
        for (auto it : mpp) {
            ll res = it.first & arr[i];
            if (res == k) {
                ans += it.second;
            }
            else if(res>k){
                rem+=3;
            }
            else{
                rem++;
            }
            curr[res] += it.second;
        }
        mpp = curr;
        mpp2=mpp;
    }
    return ans;
}
};
