class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long cnt = 0;
        int n = nums.size();
        int l = 0;
        unordered_map<long long,long long>hash1;
        for(int i = 0 ; i < n ; i++){
            unordered_map<long long,long long>hash2;
            if(nums[i] == k){
                cnt++;
            }
            hash2[nums[i]]=1;
            for(auto j: hash1){
                long long temp = j.first & nums[i];
                if(temp == k){
                    cnt = cnt + j.second;
                }
                hash2[temp] += j.second;

            }

            hash1 = hash2;
        }
        return cnt;

    }
};
