class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long c = 0;

        unordered_map<long long, long long> prevResults_subarr;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currResults_subarr; 

            if (nums[i] == k) {
                ++c;
            }
            currResults_subarr[nums[i]] = 1;

            for (auto& [val, freq] : prevResults_subarr) {
                long long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    c += freq;
                }
                currResults_subarr[newAndResult] += freq;
            }

            prevResults_subarr = currResults_subarr;
        }

        return c;
    }
};
