using i64 = long long;

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int N = nums.size();
        i64 count = 0;

        unordered_map<i64, i64> fq;
        for(int i = 0; i < N; i++) {
            if(nums[i] == k) {
                count++;
            }

            unordered_map<i64, i64> cfq;
            cfq[nums[i]] = 1;

            for(auto [v, f] : fq) {
                i64 a = (v & nums[i]);
                if(a == k) {
                    count += f;
                }
                cfq[a] += f;
            }

            fq = cfq;
        }

        return count;
    }
};
