class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        and_map = {}
        result = 0

        for i in range(n):
            new_and_map = {}
            for key, value in and_map.items():
                new_and = key & nums[i]
                if new_and in new_and_map:
                    new_and_map[new_and] += value
                else:
                    new_and_map[new_and] = value

            if nums[i] in new_and_map:
                new_and_map[nums[i]] += 1
            else:
                new_and_map[nums[i]] = 1

            and_map = new_and_map

            if k in and_map:
                result += and_map[k]

        return result
