class Solution {
public:
    long long countSubarrays(vector<int>& arr, int target) {
        int n = arr.size();
        long long ans = 0;
        std::unordered_map<long long, long long> prev;

        for (int i = 0; i < n; ++i) {
            std::unordered_map<long long, long long> curr;

            if (arr[i] == target) {
                ++ans;
            }

            curr[arr[i]] = 1;

            for (auto& [val, freq] : prev) {
                long long newAndResult = val & arr[i];
                if (newAndResult == target) {
                    ans += freq;
                }
                curr[newAndResult] += freq;
            }

            prev = curr;
        }

        return ans;
    }
};
