class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        int n=nums.size();
        unordered_map<long long,long long> mpp;
        long long ans=0;



        for(int i=0;i<n;i++){
              unordered_map<long long,long long> curr;
            if(nums[i]==k){
                ans++;
            }
            curr[nums[i]]++;
            for(auto it:mpp){
                int newand=it.first & nums[i];
                if(newand==k) ans+=it.second;
                curr[newand]+=it.second;
            }
            mpp=curr;
        }

        sort(nums.begin(),nums.end());
        return ans;
    }
};
